mapper.select:
  		select sys_id as sysId,branch,customer_num customerNum,customer_name customerName,asset_account assetAccount,rights_market rightMarket,rights_account rightsAccount,rights_code rightCode,rights_name rightsName,current_num currentNum,available_num avaliableNum,consign_sell_num consignSellNum,cost,trans_date transDate
		from hs_user_rights	where sys_id = #{sysId} 

			 and customer_num = #{customerNum}


			 and customer_name like concat(concat('%', #{customerName}), '%')


			 and rights_code = #{rightsCode}


			 and rights_name like concat(concat('%', #{rightsName}), '%')


 	 		 and trans_date >= #{transDateStart}


 	 		 and trans_date <= #{transDateEnd}

      	limit #{limit} offset #{offset}

  	select count(1) from(
  		  		  		select sys_id as sysId,branch,customer_num customerNum,customer_name customerName,asset_account assetAccount,rights_market rightMarket,rights_account rightsAccount,rights_code rightCode,rights_name rightsName,current_num currentNum,available_num avaliableNum,consign_sell_num consignSellNum,cost,trans_date transDate
		from hs_user_rights	where sys_id = #{sysId} 

			 and customer_num = #{customerNum}


			 and customer_name like concat(concat('%', #{customerName}), '%')


			 and rights_code = #{rightsCode}


			 and rights_name like concat(concat('%', #{rightsName}), '%')


 	 		 and trans_date >= #{transDateStart}


 	 		 and trans_date <= #{transDateEnd}

  	)aa
